// frontend/src/pages/Home.js
import React from "react";
import "../styles/Home.css";

// Image assets
import bgImage from "../images/bg-main.jpeg";
import gallery1 from "../images/gallery1.jpeg";
import gallery2 from "../images/gallery2.jpeg";

const Home = () => {
  return (
    <div
      className="home-container"
      style={{
        backgroundImage: `url(${bgImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        color: "#fff",
      }}
    >
      <div className="overlay">
        <main className="main-content">
          {/* LEFT PANEL - News */}
          <section className="left-panel">
            <div className="news-box">
              <h3>Latest Interview</h3>
              <p>The sound of the year. A deep dive into the artistâ€™s process.</p>
            </div>
            <div className="news-box">
              <h3>Studio Life</h3>
              <p>Behind the scenes of the latest hit-making session.</p>
            </div>
            <div className="news-box">
              <h3>Album Incoming</h3>
              <p>Everything you need to know about the next release.</p>
            </div>
          </section>

          {/* CENTER PANEL - Artist Image & Buttons */}
          <section className="center-panel">
            <img
              src={gallery1}
              alt="Main Artist"
              className="main-image"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = "/assets/images/fallback.jpeg";
              }}
            />

            <div className="buttons">
              <button
                className="btn blue"
                onClick={() => (window.location.href = "/beats")}
              >
                Listen now
              </button>
              <button
                className="btn red"
                onClick={() => alert("Share functionality coming soon!")}
              >
                Share track
              </button>
              <button
                className="btn blue-light"
                onClick={() => (window.location.href = "/news")}
              >
                Follow the show
              </button>
            </div>
          </section>

          {/* RIGHT PANEL - Photo Grid */}
          <section className="right-panel">
            {[...Array(12)].map((_, i) => (
              <img
                key={i}
                src={i % 2 === 0 ? gallery1 : gallery2}
                alt={`Thumb ${i + 1}`}
                className="thumb"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.src = "/assets/images/fallback.jpeg";
                }}
              />
            ))}
          </section>
        </main>
      </div>
    </div>
  );
};

export default Home;
